import React, { Fragment, useState } from "react";
import { useDispatch } from "react-redux";
import { Button } from "reactstrap";
import { useHistory } from "react-router-dom";
import { addUser } from "../redux/actions/constants/action-type";

const AddUser = () => {
  const [formState, setFormState] = useState({
    name: "",
    email: "",
    phone: "",
    age: "",
    gender: "",
    country: "",
    password: "",
  });

  const [error, setError] = useState("");

  const history = useHistory();
  const dispatch = useDispatch();
  const { name, email, phone, age, gender, country, password } = formState;

  const handleTextChange = (e) => {
    // const { name, value } = e.target;
    setFormState({ ...formState, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formState);
    if (!name || !email) {
      setError("Please input all input field.");
    }
    dispatch(addUser(formState));
    history.push("/");
  };

  return (
    <Fragment>
      <h2>Add User</h2>
      {error}
      <form name="form" onSubmit={handleSubmit}>
        <div>
          <div>
            <label>Name: </label>
            <input
              autoComplete="name"
              name="name"
              type="text"
              value={name}
              onChange={handleTextChange}
              autoFocus
            />
          </div>
          <div>
            <label>Email : </label>
            <input
              autoComplete="email"
              name="email"
              value={email}
              type="email"
              onChange={handleTextChange}
            />
          </div>
          <div>
            <label>Phone : </label>
            <input
              autoComplete="phone"
              name="phone"
              value={phone}
              type="number"
              onChange={handleTextChange}
            />
          </div>
          <div>
            <label>Age : </label>
            <input
              autoComplete="age"
              name="age"
              value={age}
              type="number"
              onChange={handleTextChange}
            />
          </div>
          <div name="gender" autoComplete="gender" onChange={handleTextChange}>
            <label>Gender : </label>
            <input type="radio" name="gender" value="Male" />
            <label>Male</label>
            <input type="radio" name="gender" value="Female" />
            <label>Female</label>
          </div>

          <div
            name="country"
            autoComplete="country"
            onChange={handleTextChange}
          >
            <label>Country: </label>
            <select id="country" selected={country} name="country">
              <option value="India">India</option>
              <option value="USA">USA</option>
              <option value="Canada">Canada</option>
              <option value="London">London</option>
            </select>
          </div>
          <div>
            <label>Password: </label>
            <input
              name="password"
              autoComplete="password"
              type="password"
              onChange={handleTextChange}
            />
          </div>
          <Button type="submit">Submit</Button>
          <Button onClick={() => history.push("/")}>Cancel</Button>
        </div>
      </form>
    </Fragment>
  );
};

export default AddUser;
